import React from 'react';
import Slider from 'react-slick';
import { useTranslation } from 'react-i18next';
import './EventsSlider.css';

const EventsSlider = ({ events, onViewDetails }) => {
  const { t } = useTranslation();

  const sliderSettings = {
    dots: true,
    infinite: true,
    speed: 500,
    slidesToShow: 1,
    slidesToScroll: 1,
    autoplay: true,
    autoplaySpeed: 3000,
    arrows: true,
  };

  return (
    <section id="events-slider">
      <h2>{t('home.sections.eventsSlider.title')}</h2>
      <Slider {...sliderSettings}>
        {events.map((event) => (
          <div key={event._id} className="slider-card">
            <img
              src={event.image || '/assets/default-event.jpg'} // Use a default image if event.image is missing
              alt={event.name}
              className="event-image"
            />
            <div className="slider-content">
              <h3>{event.name}</h3>
              <p>{event.description}</p>
              <p>
                {t('home.sections.eventsSlider.date')}: {new Date(event.dateOfEvent).toLocaleDateString()}
              </p>
              <button onClick={() => onViewDetails(event._id)}>
                {t('home.sections.eventsSlider.moreDetails')}
              </button>
            </div>
          </div>
        ))}
      </Slider>
    </section>
  );
};

export default EventsSlider;
