import React, { useState, useEffect } from 'react';
import { createEvent, deleteEvent, updateEvent, fetchEvents } from '../../services/api';
import './AdminDashboard.css';

function AdminDashboard() {
  const [events, setEvents] = useState([]);
  const [formData, setFormData] = useState({
    name: '',
    description: '',
    dateOfEvent: '',
    price: '',
    ticketsAvailable: '',
    purchaseStartDate: '',
    purchaseEndDate: '',
    image: null,
  });
  const [editEventId, setEditEventId] = useState(null);

  useEffect(() => {
    const loadEvents = async () => {
      const data = await fetchEvents();
      setEvents(data);
    };
    loadEvents();
  }, []);

  const handleCreateEvent = async (e) => {
    e.preventDefault();
    try {
      await createEvent(formData);
      alert('Event created successfully!');
      window.location.reload();
    } catch (error) {
      console.error('Error creating event:', error);
    }
  };

  const handleDeleteEvent = async (id) => {
    try {
      await deleteEvent(id);
      alert('Event deleted successfully!');
      setEvents(events.filter((event) => event._id !== id));
    } catch (error) {
      console.error('Error deleting event:', error);
    }
  };

  const handleUpdateEvent = async (e) => {
    e.preventDefault();
    try {
      await updateEvent(editEventId, formData);
      alert('Event updated successfully!');
      window.location.reload();
    } catch (error) {
      console.error('Error updating event:', error);
    }
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({ ...prev, [name]: value }));
  };

  const handleImageChange = (e) => {
    setFormData((prev) => ({ ...prev, image: e.target.files[0] }));
  };

  return (
    <div className="admin-dashboard">
      <h1>Admin Dashboard</h1>
      <h2>Create Event</h2>
      <form onSubmit={editEventId ? handleUpdateEvent : handleCreateEvent}>
        <input
          type="text"
          name="name"
          placeholder="Event Name"
          value={formData.name}
          onChange={handleChange}
          required
        />
        <input
          type="text"
          name="description"
          placeholder="Description"
          value={formData.description}
          onChange={handleChange}
          required
        />
        <input
          type="date"
          name="dateOfEvent"
          placeholder="Event Date"
          value={formData.dateOfEvent}
          onChange={handleChange}
          required
        />
        <input
          type="number"
          name="price"
          placeholder="Price"
          value={formData.price}
          onChange={handleChange}
          required
        />
        <input
          type="number"
          name="ticketsAvailable"
          placeholder="Tickets Available"
          value={formData.ticketsAvailable}
          onChange={handleChange}
          required
        />
        <input
          type="date"
          name="purchaseStartDate"
          placeholder="Purchase Start Date"
          value={formData.purchaseStartDate}
          onChange={handleChange}
          required
        />
        <input
          type="date"
          name="purchaseEndDate"
          placeholder="Purchase End Date"
          value={formData.purchaseEndDate}
          onChange={handleChange}
          required
        />
        <input type="file" onChange={handleImageChange} required />
        <button type="submit">{editEventId ? 'Update Event' : 'Create Event'}</button>
      </form>

      <h2>Existing Events</h2>
      <ul>
        {events.map((event) => (
          <li key={event._id}>
            <h3>{event.name}</h3>
            <p>{event.description}</p>
            <button onClick={() => handleDeleteEvent(event._id)}>Delete</button>
            <button
              onClick={() => {
                setEditEventId(event._id);
                setFormData(event);
              }}
            >
              Edit
            </button>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default AdminDashboard;
