import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { useTranslation } from 'react-i18next';
import './Header.css';

function Header() {
  const [menuOpen, setMenuOpen] = useState(false);
  const token = localStorage.getItem('token');
  const navigate = useNavigate();
  const { t, i18n } = useTranslation();

  // Load language from localStorage or default to English
  useEffect(() => {
    const savedLanguage = localStorage.getItem('language') || 'en';
    i18n.changeLanguage(savedLanguage);
  }, [i18n]);

  const handleLogout = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('role');
    navigate('/login');
  };

  const toggleMenu = () => {
    setMenuOpen(!menuOpen);
  };

  const handleScroll = (id) => {
    const section = document.getElementById(id);
    if (section) {
      section.scrollIntoView({ behavior: 'smooth' });
      setMenuOpen(false); // Close menu on mobile after clicking a link
    }
  };

  const changeLanguage = (lang) => {
    i18n.changeLanguage(lang); // Switch language dynamically
    localStorage.setItem('language', lang); // Save language in localStorage
  };

  return (
    <header className="header">
      <div className="header-container">
        <h1 className="header-title">
          <Link to="/">{t('header.title')}</Link>
        </h1>
        <button className="menu-toggle" onClick={toggleMenu}>
          ☰
        </button>
        <nav className={`header-auth-buttons ${menuOpen ? 'open' : ''}`}>
          <Link to="/events" className="nav-link">{t('header.events')}</Link>
          <a href="#about" onClick={() => handleScroll('about')} className="nav-link">
            {t('header.about')}
          </a>
          <a href="#contact" onClick={() => handleScroll('contact')} className="nav-link">
            {t('header.contact')}
          </a>
          <a href="#qa" onClick={() => handleScroll('qa')} className="nav-link">
            {t('header.qa')}
          </a>
          {token ? (
            <>
              <Link to="/profile" className="auth-button">{t('header.profile')}</Link>
              <button className="auth-button logout-button" onClick={handleLogout}>
                {t('header.logout')}
              </button>
            </>
          ) : (
            <>
              <Link to="/signup" className="auth-button">{t('header.signup')}</Link>
              <Link to="/login" className="auth-button">{t('header.login')}</Link>
            </>
          )}
        </nav>
        <div className="language-switcher">
          <button
            className={i18n.language === 'en' ? 'active' : ''}
            onClick={() => changeLanguage('en')}
          >
            English
          </button>
          <button
            className={i18n.language === 'ar' ? 'active' : ''}
            onClick={() => changeLanguage('ar')}
          >
            العربية
          </button>
        </div>
      </div>
    </header>
  );
}

export default Header;
