import React from 'react';
import { useTranslation } from 'react-i18next';
import './EventsList.css';

const EventsList = ({ events, onViewDetails }) => {
  const { t } = useTranslation();

  return (
    <section id="event-list">
      {events.map((event) => (
        <div key={event._id} className="event-card">
          <img src={event.image || '/assets/default-event.jpg'} alt={event.name} />
          <div className="event-content">
            <h3>{event.name}</h3>
            <p>{new Date(event.dateOfEvent).toLocaleDateString()}</p>
            <button onClick={() => onViewDetails(event._id)}>
              {t('home.sections.eventsSlider.moreDetails')}
            </button>
          </div>
        </div>
      ))}
    </section>
  );
};

export default EventsList;
