import React, { useState, useEffect } from 'react';
import {
  fetchEvents,
  createEvent,
  deleteEvent,
  updateEvent,
  fetchLogs,
  fetchUsers,
  updateUser,
  deleteUser,
  fetchTickets,
} from '../../services/api';
import './AdminDashboard.css';

function AdminDashboard() {
  const [currentTab, setCurrentTab] = useState('events'); // 'events', 'users', 'tickets', 'logs'
  const [events, setEvents] = useState([]);
  const [users, setUsers] = useState([]);
  const [tickets, setTickets] = useState([]);
  const [logs, setLogs] = useState([]);

  const [formData, setFormData] = useState({
    name: '',
    description: '',
    dateOfEvent: '',
    price: '',
    ticketsAvailable: '',
    purchaseStartDate: '',
    purchaseEndDate: '',
  });

  const [userFormData, setUserFormData] = useState({
    fullName: '',
    email: '',
    phoneNumber: '',
    address: {
      city: '',
      region: '',
      address1: '',
      address2: '',
    },
    age: '',
    gender: '',
    role: '',
    password: '',
  });

  const [editEventId, setEditEventId] = useState(null);
  const [editUserId, setEditUserId] = useState(null);

  // Fetch data for the current tab
  useEffect(() => {
    const loadEvents = async () => {
      if (currentTab === 'events') {
        const data = await fetchEvents();
        setEvents(data);
      }
    };

    const loadUsers = async () => {
      if (currentTab === 'users') {
        const data = await fetchUsers();
        setUsers(data);
      }
    };

    const loadTickets = async () => {
      if (currentTab === 'tickets') {
        const data = await fetchTickets();
        setTickets(data);
      }
    };

    const loadLogs = async () => {
      if (currentTab === 'logs') {
        const data = await fetchLogs();
        setLogs(data);
      }
    };

    if (currentTab === 'events') loadEvents();
    if (currentTab === 'users') loadUsers();
    if (currentTab === 'tickets') loadTickets();
    if (currentTab === 'logs') loadLogs();
  }, [currentTab]);

  // Handle Create or Update Event
  const handleEventSubmit = async (e) => {
    e.preventDefault();
    try {
      if (editEventId) {
        await updateEvent(editEventId, formData);
        alert('Event updated successfully!');
      } else {
        await createEvent(formData);
        alert('Event created successfully!');
      }
      setFormData({
        name: '',
        description: '',
        dateOfEvent: '',
        price: '',
        ticketsAvailable: '',
        purchaseStartDate: '',
        purchaseEndDate: '',
      });
      setEditEventId(null);
      const updatedEvents = await fetchEvents();
      setEvents(updatedEvents);
    } catch (error) {
      console.error('Error creating/updating event:', error);
    }
  };

  // Handle Delete Event
  const handleDeleteEvent = async (id) => {
    try {
      await deleteEvent(id);
      alert('Event deleted successfully!');
      setEvents(events.filter((event) => event._id !== id));
    } catch (error) {
      console.error('Error deleting event:', error);
    }
  };

  // Handle Edit Event
  const handleEditEvent = (event) => {
    setEditEventId(event._id);
    setFormData({
      name: event.name,
      description: event.description,
      dateOfEvent: event.dateOfEvent.slice(0, 10), // Format date for input field
      price: event.price,
      ticketsAvailable: event.ticketsAvailable,
      purchaseStartDate: event.purchaseStartDate.slice(0, 10),
      purchaseEndDate: event.purchaseEndDate.slice(0, 10),
    });
  };

  // Handle Create or Update User
  const handleUserSubmit = async (e) => {
    e.preventDefault();
    try {
      if (editUserId) {
        await updateUser(editUserId, userFormData);
        alert('User updated successfully!');
      }
      setUserFormData({
        fullName: '',
        email: '',
        phoneNumber: '',
        address: {
          city: '',
          region: '',
          address1: '',
          address2: '',
        },
        age: '',
        gender: '',
        role: '',
        password: '',
      });
      setEditUserId(null);
      const updatedUsers = await fetchUsers();
      setUsers(updatedUsers);
    } catch (error) {
      console.error('Error creating/updating user:', error);
    }
  };

  // Handle Delete User
  const handleDeleteUser = async (id) => {
    try {
      await deleteUser(id);
      alert('User deleted successfully!');
      setUsers(users.filter((user) => user._id !== id));
    } catch (error) {
      console.error('Error deleting user:', error);
    }
  };

  // Handle Edit User
  const handleEditUser = (user) => {
    setEditUserId(user._id);
    setUserFormData({
      fullName: user.fullName,
      email: user.email,
      phoneNumber: user.phoneNumber,
      address: user.address,
      age: user.age,
      gender: user.gender,
      role: user.role,
      password: '', // Password is reset to blank for security reasons
    });
  };

  return (
    <div className="admin-dashboard">
      <h1>Admin Dashboard</h1>

      {/* Tabs for Navigation */}
      <div className="admin-tabs">
        <button onClick={() => setCurrentTab('events')}>Manage Events</button>
        <button onClick={() => setCurrentTab('users')}>Manage Users</button>
        <button onClick={() => setCurrentTab('tickets')}>Manage Tickets</button>
        <button onClick={() => setCurrentTab('logs')}>View Logs</button>
      </div>

      {/* Events Tab */}
      {currentTab === 'events' && (
        <div>
          <h2>{editEventId ? 'Edit Event' : 'Create Event'}</h2>
          <form onSubmit={handleEventSubmit}>
            <input
              type="text"
              name="name"
              placeholder="Event Name"
              value={formData.name}
              onChange={(e) => setFormData({ ...formData, name: e.target.value })}
              required
            />
            {/* Other Event Inputs */}
            <button type="submit">{editEventId ? 'Update Event' : 'Create Event'}</button>
          </form>

          <h2>Existing Events</h2>
          <ul>
            {events.map((event) => (
              <li key={event._id}>
                <h3>{event.name}</h3>
                <button onClick={() => handleEditEvent(event)}>Edit</button>
                <button onClick={() => handleDeleteEvent(event._id)}>Delete</button>
              </li>
            ))}
          </ul>
        </div>
      )}

      {/* Users Tab */}
      {currentTab === 'users' && (
        <div>
          <h2>{editUserId ? 'Edit User' : 'Manage Users'}</h2>
          <form onSubmit={handleUserSubmit}>
            <input
              type="text"
              name="fullName"
              placeholder="Full Name"
              value={userFormData.fullName}
              onChange={(e) => setUserFormData({ ...userFormData, fullName: e.target.value })}
              required
            />
            <input
              type="email"
              name="email"
              placeholder="Email"
              value={userFormData.email}
              onChange={(e) => setUserFormData({ ...userFormData, email: e.target.value })}
              required
            />
            <input
              type="text"
              name="phoneNumber"
              placeholder="Phone Number"
              value={userFormData.phoneNumber}
              onChange={(e) => setUserFormData({ ...userFormData, phoneNumber: e.target.value })}
              required
            />
            <input
              type="number"
              name="age"
              placeholder="Age"
              value={userFormData.age}
              onChange={(e) => setUserFormData({ ...userFormData, age: e.target.value })}
              required
            />
            <select
              name="gender"
              value={userFormData.gender}
              onChange={(e) => setUserFormData({ ...userFormData, gender: e.target.value })}
              required
            >
              <option value="">Select Gender</option>
              <option value="male">Male</option>
              <option value="female">Female</option>
              <option value="other">Other</option>
            </select>
            <input
              type="text"
              name="role"
              placeholder="Role"
              value={userFormData.role}
              onChange={(e) => setUserFormData({ ...userFormData, role: e.target.value })}
              required
            />
            <input
              type="password"
              name="password"
              placeholder="Password"
              value={userFormData.password}
              onChange={(e) => setUserFormData({ ...userFormData, password: e.target.value })}
            />
            <button type="submit">{editUserId ? 'Update User' : 'Add User'}</button>
          </form>

          <h2>Existing Users</h2>
          <ul>
            {users.map((user) => (
              <li key={user._id}>
                <h3>{user.fullName}</h3>
                <button onClick={() => handleEditUser(user)}>Edit</button>
                <button onClick={() => handleDeleteUser(user._id)}>Delete</button>
              </li>
            ))}
          </ul>
        </div>
      )}
    </div>
  );
}

export default AdminDashboard;
