import React, { useState, useEffect } from 'react';
import { fetchEvents, createEvent, deleteEvent, updateEvent, fetchLogs, fetchUsers, fetchTickets } from '../../services/api';
import './AdminDashboard.css';

function AdminDashboard() {
  const [currentTab, setCurrentTab] = useState('events'); // 'events', 'users', 'tickets', 'logs'
  const [events, setEvents] = useState([]);
  const [users, setUsers] = useState([]);
  const [tickets, setTickets] = useState([]);
  const [logs, setLogs] = useState([]);
  const [formData, setFormData] = useState({
    name: '',
    description: '',
    dateOfEvent: '',
    price: '',
    ticketsAvailable: '',
    purchaseStartDate: '',
    purchaseEndDate: '',
    image: null,
  });
  const [editEventId, setEditEventId] = useState(null);

  useEffect(() => {
    const loadEvents = async () => {
      if (currentTab === 'events') {
        const data = await fetchEvents();
        setEvents(data);
      }
    };

    const loadUsers = async () => {
      if (currentTab === 'users') {
        const data = await fetchUsers();
        setUsers(data);
      }
    };

    const loadTickets = async () => {
      if (currentTab === 'tickets') {
        const data = await fetchTickets();
        setTickets(data);
      }
    };

    const loadLogs = async () => {
      if (currentTab === 'logs') {
        const data = await fetchLogs();
        setLogs(data);
      }
    };

    if (currentTab === 'events') loadEvents();
    if (currentTab === 'users') loadUsers();
    if (currentTab === 'tickets') loadTickets();
    if (currentTab === 'logs') loadLogs();
  }, [currentTab]);

  const handleCreateEvent = async (e) => {
    e.preventDefault();
    try {
      await createEvent(formData);
      alert('Event created successfully!');
      setCurrentTab('events'); // Redirect to events tab
    } catch (error) {
      console.error('Error creating event:', error);
    }
  };

  const handleDeleteEvent = async (id) => {
    try {
      await deleteEvent(id);
      alert('Event deleted successfully!');
      setEvents(events.filter((event) => event._id !== id));
    } catch (error) {
      console.error('Error deleting event:', error);
    }
  };

  const handleUpdateEvent = async (e) => {
    e.preventDefault();
    try {
      await updateEvent(editEventId, formData);
      alert('Event updated successfully!');
      setEditEventId(null);
      setCurrentTab('events');
    } catch (error) {
      console.error('Error updating event:', error);
    }
  };

  return (
    <div className="admin-dashboard">
      <h1>Admin Dashboard</h1>

      {/* Tabs for Navigation */}
      <div className="admin-tabs">
        <button onClick={() => setCurrentTab('events')}>Manage Events</button>
        <button onClick={() => setCurrentTab('users')}>Manage Users</button>
        <button onClick={() => setCurrentTab('tickets')}>Manage Tickets</button>
        <button onClick={() => setCurrentTab('logs')}>View Logs</button>
      </div>

      {/* Events Tab */}
      {currentTab === 'events' && (
        <div>
          <h2>Manage Events</h2>
          <form onSubmit={editEventId ? handleUpdateEvent : handleCreateEvent}>
            <input type="text" name="name" placeholder="Event Name" value={formData.name} onChange={(e) => setFormData({ ...formData, name: e.target.value })} required />
            <input type="text" name="description" placeholder="Description" value={formData.description} onChange={(e) => setFormData({ ...formData, description: e.target.value })} required />
            <input type="date" name="dateOfEvent" value={formData.dateOfEvent} onChange={(e) => setFormData({ ...formData, dateOfEvent: e.target.value })} required />
            <input type="number" name="price" value={formData.price} onChange={(e) => setFormData({ ...formData, price: e.target.value })} required />
            <input type="file" name="image" onChange={(e) => setFormData({ ...formData, image: e.target.files[0] })} />
            <button type="submit">{editEventId ? 'Update Event' : 'Create Event'}</button>
          </form>

          <ul>
            {events.map((event) => (
              <li key={event._id}>
                <h3>{event.name}</h3>
                <button onClick={() => handleDeleteEvent(event._id)}>Delete</button>
                <button onClick={() => setEditEventId(event._id)}>Edit</button>
              </li>
            ))}
          </ul>
        </div>
      )}

      {/* Users Tab */}
      {currentTab === 'users' && (
        <div>
          <h2>Manage Users</h2>
          <ul>
            {users.map((user) => (
              <li key={user._id}>
                {user.fullName} - {user.email}
              </li>
            ))}
          </ul>
        </div>
      )}

      {/* Tickets Tab */}
      {currentTab === 'tickets' && (
        <div>
          <h2>Manage Tickets</h2>
          <ul>
            {tickets.map((ticket) => (
              <li key={ticket._id}>
                Ticket for Event ID: {ticket.eventId}
              </li>
            ))}
          </ul>
        </div>
      )}

      {/* Logs Tab */}
      {currentTab === 'logs' && (
        <div>
          <h2>View Logs</h2>
          <ul>
            {logs.map((log) => (
              <li key={log._id}>
                {log.action} - {log.timestamp}
              </li>
            ))}
          </ul>
        </div>
      )}
    </div>
  );
}

export default AdminDashboard;
