import React, { useState, useEffect } from 'react';
import { fetchEvents, createEvent, updateEvent, deleteEvent, fetchOrganizers } from '../../../services/api';

function EventsTab() {
  const [events, setEvents] = useState([]);
  const [organizers, setOrganizers] = useState([]);
  const [formData, setFormData] = useState({
    name: '',
    description: '',
    dateOfEvent: '',
    price: '',
    ticketsAvailable: '',
    purchaseStartDate: '',
    purchaseEndDate: '',
    organizers: [],
  });
  const [editEventId, setEditEventId] = useState(null);

  useEffect(() => {
    const loadEvents = async () => {
      const data = await fetchEvents();
      setEvents(data);
    };

    const loadOrganizers = async () => {
      const data = await fetchOrganizers();
      setOrganizers(data);
    };

    loadEvents();
    loadOrganizers();
  }, []);

  const handleEventSubmit = async (e) => {
    e.preventDefault();
    try {
      if (editEventId) {
        await updateEvent(editEventId, formData);
        alert('Event updated successfully!');
      } else {
        await createEvent(formData);
        alert('Event created successfully!');
      }
      setFormData({
        name: '',
        description: '',
        dateOfEvent: '',
        price: '',
        ticketsAvailable: '',
        purchaseStartDate: '',
        purchaseEndDate: '',
        organizers: [],
      });
      setEditEventId(null);
      const updatedEvents = await fetchEvents();
      setEvents(updatedEvents);
    } catch (error) {
      console.error('Error creating/updating event:', error);
    }
  };

  const handleEditEvent = (event) => {
    setEditEventId(event._id);
    setFormData({
      name: event.name,
      description: event.description,
      dateOfEvent: event.dateOfEvent.slice(0, 10),
      price: event.price,
      ticketsAvailable: event.ticketsAvailable,
      purchaseStartDate: event.purchaseStartDate.slice(0, 10),
      purchaseEndDate: event.purchaseEndDate.slice(0, 10),
      organizers: event.organizers.map((org) => org._id),
    });
  };

  const handleDeleteEvent = async (id) => {
    try {
      await deleteEvent(id);
      alert('Event deleted successfully!');
      setEvents(events.filter((event) => event._id !== id));
    } catch (error) {
      console.error('Error deleting event:', error);
    }
  };

  return (
    <div>
      <h2>{editEventId ? 'Edit Event' : 'Create Event'}</h2>
      <form onSubmit={handleEventSubmit}>
        <input
          type="text"
          name="name"
          placeholder="Event Name"
          value={formData.name}
          onChange={(e) => setFormData({ ...formData, name: e.target.value })}
          required
        />
        <textarea
          name="description"
          placeholder="Description"
          value={formData.description}
          onChange={(e) => setFormData({ ...formData, description: e.target.value })}
          required
        />
        <input
          type="date"
          name="dateOfEvent"
          value={formData.dateOfEvent}
          onChange={(e) => setFormData({ ...formData, dateOfEvent: e.target.value })}
          required
        />
        <input
          type="number"
          name="price"
          placeholder="Price"
          value={formData.price}
          onChange={(e) => setFormData({ ...formData, price: e.target.value })}
          required
        />
        <input
          type="number"
          name="ticketsAvailable"
          placeholder="Tickets Available"
          value={formData.ticketsAvailable}
          onChange={(e) => setFormData({ ...formData, ticketsAvailable: e.target.value })}
          required
        />
        <input
          type="date"
          name="purchaseStartDate"
          value={formData.purchaseStartDate}
          onChange={(e) => setFormData({ ...formData, purchaseStartDate: e.target.value })}
          required
        />
        <input
          type="date"
          name="purchaseEndDate"
          value={formData.purchaseEndDate}
          onChange={(e) => setFormData({ ...formData, purchaseEndDate: e.target.value })}
          required
        />
        <div>
          <label>Organizers:</label>
          <select
            multiple
            value={formData.organizers}
            onChange={(e) =>
              setFormData({
                ...formData,
                organizers: [...e.target.selectedOptions].map((option) => option.value),
              })
            }
          >
            {organizers.map((organizer) => (
              <option key={organizer._id} value={organizer._id}>
                {organizer.fullName} ({organizer.email})
              </option>
            ))}
          </select>
        </div>
        <button type="submit">{editEventId ? 'Update Event' : 'Create Event'}</button>
      </form>

      <h2>Existing Events</h2>
      <ul>
        {events.map((event) => (
          <li key={event._id}>
            <h3>{event.name}</h3>
            <button onClick={() => handleEditEvent(event)}>Edit</button>
            <button onClick={() => handleDeleteEvent(event._id)}>Delete</button>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default EventsTab;
