import React, { useState, useEffect } from 'react';
import { useTranslation } from 'react-i18next';
import { useNavigate } from 'react-router-dom';
import logo from '../pages/assets/saada.png';
import './Header.css';
import AboutSection from '../pages/Sections/AboutSection';
import FAQSection from '../pages/Sections/QASection';
import ContactSection from '../pages/Sections/ContactSection';

function Header() {
  const [activeSection, setActiveSection] = useState(''); // State to manage active section
  const token = localStorage.getItem('token');
  const navigate = useNavigate();
  const { t, i18n } = useTranslation();

  useEffect(() => {
    const savedLanguage = localStorage.getItem('language') || 'en';
    i18n.changeLanguage(savedLanguage);
  }, [i18n]);

  const handleLogout = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('role');
    navigate('/login');
  };

  const toggleLanguage = () => {
    const newLanguage = i18n.language === 'en' ? 'ar' : 'en';
    i18n.changeLanguage(newLanguage);
    localStorage.setItem('language', newLanguage);
  };

  const renderActiveSection = () => {
    switch (activeSection) {
      case 'about':
        return <AboutSection />;
      case 'faqs':
        return <FAQSection />;
      case 'contact':
        return <ContactSection />;
      default:
        return null;
    }
  };

  return (
    <>
      <header className={`header ${i18n.language === 'ar' ? 'rtl' : ''}`}>
        {/* Top Header */}
        <div className="top_header">
          <div className="header-container">
            <div className="logo_site">
              <img src={logo} alt="Logo" onClick={() => navigate('/')} />
            </div>
            <div className="find_us_block">
              <p>{t('header.find_us_on')}:</p>
              <ul className="social_head">
                <li>
                  <a href="https://facebook.com">
                    <i className="fab fa-facebook-f"></i>
                  </a>
                </li>
                <li>
                  <a href="https://twitter.com">
                    <i className="fab fa-twitter"></i>
                  </a>
                </li>
                <li>
                  <a href="https://instagram.com">
                    <i className="fab fa-instagram"></i>
                  </a>
                </li>
              </ul>
            </div>
          </div>
        </div>

        {/* Bottom Header */}
        <div className="bottom_header">
          <div className="header-container">
            <ul className="main_menu">
              <li>
                <button
                  className="nav-link"
                  onClick={() => setActiveSection('about')}
                >
                  {t('header.about')}
                </button>
              </li>
              <li>
                <button
                  className="nav-link"
                  onClick={() => setActiveSection('faqs')}
                >
                  {t('header.faqs')}
                </button>
              </li>
              <li>
                <button
                  className="nav-link"
                  onClick={() => setActiveSection('contact')}
                >
                  {t('header.contact')}
                </button>
              </li>
              {token && (
                <li>
                  <button
                    className="profile-button"
                    onClick={() => navigate('/profile')}
                  >
                    {t('header.profile')}
                  </button>
                </li>
              )}
            </ul>
            <div className="auth_buttons">
              {!token ? (
                <>
                  <button
                    className="auth-button login"
                    onClick={() => navigate('/login')}
                  >
                    <i className="fas fa-sign-in-alt"></i> {t('header.login')}
                  </button>
                  <button
                    className="auth-button signup"
                    onClick={() => navigate('/signup')}
                  >
                    <i className="fas fa-user-plus"></i> {t('header.signup')}
                  </button>
                </>
              ) : (
                <button onClick={handleLogout} className="auth-button logout">
                  <i className="fas fa-sign-out-alt"></i> {t('header.logout')}
                </button>
              )}
            </div>
            <div className="lang_site">
              <button onClick={toggleLanguage} className="lang-button">
                {i18n.language === 'en' ? 'العربية' : 'English'}
              </button>
            </div>
          </div>
        </div>
      </header>

      {/* Render the active section */}
      <main>{renderActiveSection()}</main>
    </>
  );
}

export default Header;
