import React, { useEffect, useState } from "react";
import { useTranslation } from "react-i18next";
import { fetchEvents } from "../services/api";
import { useNavigate } from "react-router-dom";
import "./EventsPage.css";

function EventsPage() {
  const { t } = useTranslation();
  const [events, setEvents] = useState([]);
  const [filteredEvents, setFilteredEvents] = useState([]);
  const [category, setCategory] = useState("");
  const [date, setDate] = useState("");
  const navigate = useNavigate();

  useEffect(() => {
    const getEvents = async () => {
      const data = await fetchEvents();
      setEvents(data);
      setFilteredEvents(data);
    };

    getEvents();
  }, []);

  const handleFilter = () => {
    let filtered = [...events];
    if (category) {
      filtered = filtered.filter((event) => event.category === category);
    }
    if (date) {
      filtered = filtered.filter((event) => event.dateOfEvent.startsWith(date));
    }
    setFilteredEvents(filtered);
  };

  const resetFilter = () => {
    setCategory("");
    setDate("");
    setFilteredEvents(events);
  };

  const handleViewDetails = (id) => {
    navigate(`/event/${id}`);
  };

  return (
    <div className="events-page">
      {/* Sidebar for Filters */}
      <div className="sidebar">
        <h2>{t("eventsPage.filter.title")}</h2>
        <div className="filter-group">
          <label>{t("eventsPage.filter.category")}</label>
          <select
            value={category}
            onChange={(e) => setCategory(e.target.value)}
          >
            <option value="">{t("eventsPage.filter.selectCategory")}</option>
            <option value="Sporting">Sporting</option>
            <option value="Business">Business</option>
            <option value="Training">Training</option>
          </select>
        </div>
        <div className="filter-group">
          <label>{t("eventsPage.filter.date")}</label>
          <input
            type="date"
            value={date}
            onChange={(e) => setDate(e.target.value)}
          />
        </div>
        <div className="filter-group">
          <button onClick={handleFilter}>{t("eventsPage.filter.filterNow")}</button>
          <button onClick={resetFilter}>{t("eventsPage.filter.reset")}</button>
        </div>
      </div>

      {/* Events Grid */}
      <div className="events-grid">
        {filteredEvents.map((event) => (
          <div className="event-card" key={event._id}>
            <img src={event.image || "/placeholder.jpg"} alt={event.name} />
            <h3>{event.name}</h3>
            <p>{t("eventsPage.eventCard.date")}: {new Date(event.dateOfEvent).toLocaleDateString()}</p>
            <button onClick={() => handleViewDetails(event._id)}>
              {t("eventsPage.eventCard.booking")}
            </button>
          </div>
        ))}
      </div>
    </div>
  );
}

export default EventsPage;
