import React, { useEffect, useState } from "react";
import { useTranslation } from "react-i18next";
import { fetchEvents } from "../services/api";
import { useNavigate } from "react-router-dom";
import "./EventsPage.css";

function EventsPage() {
  const { t } = useTranslation();
  const [events, setEvents] = useState([]);
  const [filteredEvents, setFilteredEvents] = useState([]);
  const [filters, setFilters] = useState({
    category: "",
    date: "",
    type: "available", // available or past
  });

  const navigate = useNavigate();

  useEffect(() => {
    const getEvents = async () => {
      const data = await fetchEvents();
      setEvents(data);
      setFilteredEvents(data);
    };

    getEvents();
  }, []);

  const handleFilter = () => {
    let filtered = events;
    if (filters.category) {
      filtered = filtered.filter((event) =>
        event.category.toLowerCase().includes(filters.category.toLowerCase())
      );
    }
    if (filters.date) {
      filtered = filtered.filter(
        (event) => new Date(event.dateOfEvent).toDateString() === new Date(filters.date).toDateString()
      );
    }
    if (filters.type === "available") {
      filtered = filtered.filter((event) => event.isAvailable);
    } else if (filters.type === "past") {
      filtered = filtered.filter((event) => !event.isAvailable);
    }
    setFilteredEvents(filtered);
  };

  const handleReset = () => {
    setFilters({ category: "", date: "", type: "available" });
    setFilteredEvents(events);
  };

  const handleViewDetails = (id) => {
    navigate(`/event/${id}`);
  };

  return (
    <div className="events-page">
      <div className="events-header">
        <h1>{t("eventsPage.title")}</h1>
        <p>{filteredEvents.length} {t("eventsPage.results")}</p>
      </div>
      <div className="filters-container">
        <select
          value={filters.category}
          onChange={(e) => setFilters({ ...filters, category: e.target.value })}
        >
          <option value="">{t("eventsPage.filters.categoryPlaceholder")}</option>
          <option value="sporting">{t("eventsPage.filters.sporting")}</option>
          <option value="business">{t("eventsPage.filters.business")}</option>
          <option value="training">{t("eventsPage.filters.training")}</option>
        </select>
        <div>
          <label>
            <input
              type="radio"
              name="type"
              value="available"
              checked={filters.type === "available"}
              onChange={() => setFilters({ ...filters, type: "available" })}
            />
            {t("eventsPage.filters.available")}
          </label>
          <label>
            <input
              type="radio"
              name="type"
              value="past"
              checked={filters.type === "past"}
              onChange={() => setFilters({ ...filters, type: "past" })}
            />
            {t("eventsPage.filters.past")}
          </label>
        </div>
        <input
          type="date"
          value={filters.date}
          onChange={(e) => setFilters({ ...filters, date: e.target.value })}
        />
        <button onClick={handleFilter}>{t("eventsPage.filters.filterNow")}</button>
        <button onClick={handleReset}>{t("eventsPage.filters.reset")}</button>
      </div>
      <div className="events-grid">
        {filteredEvents.map((event) => (
          <div className="event-card" key={event._id}>
            <h3>{event.name}</h3>
            <p className="event-date">{new Date(event.dateOfEvent).toLocaleDateString()}</p>
            <p>{event.description}</p>
            <button onClick={() => handleViewDetails(event._id)}>
              {t("eventsPage.eventCard.booking")}
            </button>
          </div>
        ))}
      </div>
    </div>
  );
}

export default EventsPage;
