import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { useTranslation } from 'react-i18next';
import './Header.css';

function Header() {
  const [menuOpen, setMenuOpen] = useState(false);
  const token = localStorage.getItem('token'); // Check if user is logged in
  const navigate = useNavigate();
  const { t, i18n } = useTranslation();

  useEffect(() => {
    const savedLanguage = localStorage.getItem('language') || 'en';
    i18n.changeLanguage(savedLanguage);
  }, [i18n]);

  const handleLogout = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('role');
    navigate('/login');
  };

  const toggleMenu = () => {
    setMenuOpen(!menuOpen);
  };

  const handleScroll = (id) => {
    const section = document.getElementById(id);
    if (section) {
      section.scrollIntoView({ behavior: 'smooth' });
      setMenuOpen(false); // Close menu on mobile after clicking a link
    }
  };

  const changeLanguage = (lang) => {
    i18n.changeLanguage(lang);
    localStorage.setItem('language', lang);
  };

  return (
    <header className="header">
      {/* Top Header */}
      <div className="top_header">
        <div className="header-container">
          <div className="logo_site">
            <Link to="/">
              <img src="/path-to-logo.png" alt="Logo" />
            </Link>
          </div>
          <div className="find_us_block">
            <p>{t('header.find_us_on')}:</p>
            <ul className="social_head">
              <li>
                <a href="https://facebook.com">
                  <i className="fab fa-facebook-f"></i>
                </a>
              </li>
              <li>
                <a href="https://twitter.com">
                  <i className="fab fa-twitter"></i>
                </a>
              </li>
              <li>
                <a href="https://instagram.com">
                  <i className="fab fa-instagram"></i>
                </a>
              </li>
            </ul>
          </div>
        </div>
      </div>

      {/* Bottom Header */}
      <div className="bottom_header">
        <div className="header-container">
          <ul className="main_menu">
            <li>
              <Link to="/" className="nav-link">
                {t('header.home')}
              </Link>
            </li>
            <li>
              <Link to="/events" className="nav-link">
                {t('header.events')}
              </Link>
            </li>
            <li>
              <a href="#about" onClick={() => handleScroll('about')} className="nav-link">
                {t('header.about')}
              </a>
            </li>
            <li>
              <a href="#faq" onClick={() => handleScroll('qa')} className="nav-link">
                {t('header.faqs')}
              </a>
            </li>
            <li>
              <a href="#contact" onClick={() => handleScroll('contact')} className="nav-link">
                {t('header.contact')}
              </a>
            </li>
            {token && (
              <li>
                <Link to="/profile" className="nav-link profile-button">
                  {t('header.profile')}
                </Link>
              </li>
            )}
          </ul>
          <div className="auth_buttons">
            {!token ? (
              <>
                <Link to="/login" className="auth-button">
                  <i className="fas fa-sign-in-alt"></i> {t('header.login')}
                </Link>
                <Link to="/signup" className="auth-button">
                  <i className="fas fa-user-plus"></i> {t('header.signup')}
                </Link>
              </>
            ) : (
              <button onClick={handleLogout} className="auth-button logout">
                <i className="fas fa-sign-out-alt"></i> {t('header.logout')}
              </button>
            )}
          </div>
          <div className="lang_site">
            <button
              className={`lang_button ${i18n.language === 'en' ? 'active' : ''}`}
              onClick={() => changeLanguage('en')}
            >
              <img src="/path-to-en-flag.png" alt="English" /> English
            </button>
            <button
              className={`lang_button ${i18n.language === 'ar' ? 'active' : ''}`}
              onClick={() => changeLanguage('ar')}
            >
              <img src="/path-to-ar-flag.png" alt="Arabic" /> العربية
            </button>
          </div>
        </div>
      </div>
    </header>
  );
}

export default Header;
