import React, { useState, useEffect } from 'react';
import {
  fetchEvents,
  createEvent,
  deleteEvent,
  updateEvent,
  fetchLogs,
  fetchUsers,
  fetchTickets,
} from '../../services/api';
import './AdminDashboard.css';

function AdminDashboard() {
  const [currentTab, setCurrentTab] = useState('events'); // 'events', 'users', 'tickets', 'logs'
  const [events, setEvents] = useState([]);
  const [users, setUsers] = useState([]);
  const [tickets, setTickets] = useState([]);
  const [logs, setLogs] = useState([]);
  const [formData, setFormData] = useState({
    name: '',
    description: '',
    dateOfEvent: '',
    price: '',
    ticketsAvailable: '',
    purchaseStartDate: '',
    purchaseEndDate: '',
  });
  const [editEventId, setEditEventId] = useState(null);

  // Fetch data for the current tab
  useEffect(() => {
    const loadEvents = async () => {
      if (currentTab === 'events') {
        const data = await fetchEvents();
        setEvents(data);
      }
    };

    const loadUsers = async () => {
      if (currentTab === 'users') {
        const data = await fetchUsers();
        setUsers(data);
      }
    };

    const loadTickets = async () => {
      if (currentTab === 'tickets') {
        const data = await fetchTickets();
        setTickets(data);
      }
    };

    const loadLogs = async () => {
      if (currentTab === 'logs') {
        const data = await fetchLogs();
        setLogs(data);
      }
    };

    if (currentTab === 'events') loadEvents();
    if (currentTab === 'users') loadUsers();
    if (currentTab === 'tickets') loadTickets();
    if (currentTab === 'logs') loadLogs();
  }, [currentTab]);

  // Handle Create or Update Event
  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      if (editEventId) {
        await updateEvent(editEventId, formData);
        alert('Event updated successfully!');
      } else {
        await createEvent(formData);
        alert('Event created successfully!');
      }
      setFormData({
        name: '',
        description: '',
        dateOfEvent: '',
        price: '',
        ticketsAvailable: '',
        purchaseStartDate: '',
        purchaseEndDate: '',
      });
      setEditEventId(null);
      const updatedEvents = await fetchEvents();
      setEvents(updatedEvents);
    } catch (error) {
      console.error('Error creating/updating event:', error);
    }
  };

  // Handle Delete Event
  const handleDelete = async (id) => {
    try {
      await deleteEvent(id);
      alert('Event deleted successfully!');
      setEvents(events.filter((event) => event._id !== id));
    } catch (error) {
      console.error('Error deleting event:', error);
    }
  };

  // Handle Edit Event
  const handleEdit = (event) => {
    setEditEventId(event._id);
    setFormData({
      name: event.name,
      description: event.description,
      dateOfEvent: event.dateOfEvent.slice(0, 10), // Format date for input field
      price: event.price,
      ticketsAvailable: event.ticketsAvailable,
      purchaseStartDate: event.purchaseStartDate.slice(0, 10),
      purchaseEndDate: event.purchaseEndDate.slice(0, 10),
    });
  };

  return (
    <div className="admin-dashboard">
      <h1>Admin Dashboard</h1>

      {/* Tabs for Navigation */}
      <div className="admin-tabs">
        <button onClick={() => setCurrentTab('events')}>Manage Events</button>
        <button onClick={() => setCurrentTab('users')}>Manage Users</button>
        <button onClick={() => setCurrentTab('tickets')}>Manage Tickets</button>
        <button onClick={() => setCurrentTab('logs')}>View Logs</button>
      </div>

      {/* Events Tab */}
      {currentTab === 'events' && (
        <div>
          <h2>{editEventId ? 'Edit Event' : 'Create Event'}</h2>
          <form onSubmit={handleSubmit}>
            <input
              type="text"
              name="name"
              placeholder="Event Name"
              value={formData.name}
              onChange={(e) => setFormData({ ...formData, name: e.target.value })}
              required
            />
            <textarea
              name="description"
              placeholder="Description"
              value={formData.description}
              onChange={(e) => setFormData({ ...formData, description: e.target.value })}
              required
            />
            <input
              type="date"
              name="dateOfEvent"
              value={formData.dateOfEvent}
              onChange={(e) => setFormData({ ...formData, dateOfEvent: e.target.value })}
              required
            />
            <input
              type="number"
              name="price"
              placeholder="Price"
              value={formData.price}
              onChange={(e) => setFormData({ ...formData, price: e.target.value })}
              required
            />
            <input
              type="number"
              name="ticketsAvailable"
              placeholder="Tickets Available"
              value={formData.ticketsAvailable}
              onChange={(e) => setFormData({ ...formData, ticketsAvailable: e.target.value })}
              required
            />
            <input
              type="date"
              name="purchaseStartDate"
              value={formData.purchaseStartDate}
              onChange={(e) => setFormData({ ...formData, purchaseStartDate: e.target.value })}
              required
            />
            <input
              type="date"
              name="purchaseEndDate"
              value={formData.purchaseEndDate}
              onChange={(e) => setFormData({ ...formData, purchaseEndDate: e.target.value })}
              required
            />
            <button type="submit">{editEventId ? 'Update Event' : 'Create Event'}</button>
          </form>

          <h2>Existing Events</h2>
          <ul>
            {events.map((event) => (
              <li key={event._id}>
                <h3>{event.name}</h3>
                <p>{event.description}</p>
                <button onClick={() => handleEdit(event)}>Edit</button>
                <button onClick={() => handleDelete(event._id)}>Delete</button>
              </li>
            ))}
          </ul>
        </div>
      )}

      {/* Users Tab */}
      {currentTab === 'users' && (
        <div>
          <h2>Manage Users</h2>
          <ul>
            {users.map((user) => (
              <li key={user._id}>
                {user.fullName} - {user.email}
              </li>
            ))}
          </ul>
        </div>
      )}

      {/* Tickets Tab */}
      {currentTab === 'tickets' && (
        <div>
          <h2>Manage Tickets</h2>
          <ul>
            {tickets.map((ticket) => (
              <li key={ticket._id}>
                Ticket for Event ID: {ticket.eventId}
              </li>
            ))}
          </ul>
        </div>
      )}

      {/* Logs Tab */}
      {currentTab === 'logs' && (
        <div>
          <h2>View Logs</h2>
          <ul>
            {logs.map((log) => (
              <li key={log._id}>
                {log.action} - {log.timestamp}
              </li>
            ))}
          </ul>
        </div>
      )}
    </div>
  );
}

export default AdminDashboard;
