import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { fetchEvents } from '../services/api';
import EventsSection from './Sections/EventsSection';
import AboutSection from './Sections/AboutSection';
import ContactSection from './Sections/ContactSection';
import QASection from './Sections/QASection';
import './Home.css';
import Slider from 'react-slick';

function Home() {
  const [events, setEvents] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    const getEvents = async () => {
      try {
        const data = await fetchEvents();
        setEvents(data);
      } catch (error) {
        console.error('Error fetching events:', error);
      }
    };

    getEvents();
  }, []);

  const handleViewDetails = (id) => {
    navigate(`/event/${id}`);
  };

  const settings = {
    dots: true,
    infinite: true,
    speed: 500,
    slidesToShow: 1,
    slidesToScroll: 1,
    autoplay: true,
    autoplaySpeed: 4000,
    arrows: true,
    responsive: [
      {
        breakpoint: 768,
        settings: {
          arrows: false,
          dots: true,
        },
      },
    ],
  };

  return (
    <div className="home">
    {/* Events Section */}
    <section id="events">
      <h2 className="section-title">Upcoming Events</h2>
      <Slider {...settings}>
        {events.map((event) => (
          <div className="event-slide" key={event._id}>
            <div className="event-slide-content">
              <div className="event-image" style={{ backgroundImage: `url(${event.image})` }}></div>
              <div className="event-details">
                <h3>{event.name}</h3>
                <p>{event.description}</p>
                <p>Date: {new Date(event.dateOfEvent).toLocaleDateString()}</p>
                <p>Price: {event.currency} {event.price}</p>
                <button onClick={() => handleViewDetails(event._id)}>More Details</button>
              </div>
            </div>
          </div>
        ))}
      </Slider>
    </section>
       <AboutSection />
      <ContactSection />
      <QASection />
    </div>
  );
}

export default Home;
