import React from 'react';
import Slider from 'react-slick';
import './EventsSlider.css';

const EventsSlider = ({ events, onViewDetails }) => {
  const sliderSettings = {
    dots: true,
    infinite: true,
    speed: 500,
    slidesToShow: 1,
    slidesToScroll: 1,
    autoplay: true,
    autoplaySpeed: 3000,
    arrows: true,
  };

  if (!events.length) {
    return <div className="no-events">No events available</div>;
  }

  return (
    <section id="events-slider">
      <Slider {...sliderSettings}>
        {events.map((event) => (
          <div key={event._id} className="slider-card">
            <img
              src={event.image || '/assets/default-event.jpg'}
              alt={event.name}
              className="event-image"
            />
            <div className="slider-content">
              <h3>{event.name}</h3>
              <p>{event.description}</p>
              <p>Date: {new Date(event.dateOfEvent).toLocaleDateString()}</p>
              <button onClick={() => onViewDetails(event._id)}>View Details</button>
            </div>
          </div>
        ))}
      </Slider>
    </section>
  );
};

export default EventsSlider;
