// Import axios for making HTTP requests
import axios from 'axios';

// Set up a base instance with the backend URL from .env
const api = axios.create({
  baseURL: process.env.REACT_APP_BACKEND_URL || 'http://localhost:5000',
});
// Add a request interceptor to include the Authorization header
api.interceptors.request.use(
  (config) => {
    const token = localStorage.getItem('token');
    if (token) {
      config.headers.Authorization = `Bearer ${token}`;
    }
    return config;
  },
  (error) => {
    return Promise.reject(error);
  }
);
// Define functions to interact with your backend API
export const fetchEvents = async () => {
  try {
    const response = await api.get('/api/events');
    return response.data;
  } catch (error) {
    console.error('Error fetching events:', error);
    throw error;
  }
};

  export const registerUser = async (userData) => {
    return await api.post('/api/auth/register', userData);
  };
  
  export const loginUser = async (credentials) => {
    return await api.post('/api/auth/login', credentials);
  };


// Create an Event
export const createEvent = async (eventData) => {
  const formData = new FormData();
  Object.keys(eventData).forEach((key) => {
    if (key === 'image') {
      formData.append(key, eventData[key]);
    } else {
      formData.append(key, eventData[key]);
    }
  });
  return await api.post('/api/events', formData, {
    headers: {
      'Content-Type': 'multipart/form-data',
    },
  });
};

// Delete an Event
export const deleteEvent = async (eventId) => {
  return await api.delete(`/api/events/${eventId}`);
};

// Update an Event
export const updateEvent = async (eventId, updates) => {
  return await api.put(`/api/events/${eventId}`, updates);
};

  // Export the instance for custom requests if needed
export default api;
