import React, { useState, useEffect } from 'react';
import { useTranslation } from 'react-i18next';
import { fetchEvents, createEvent, updateEvent, deleteEvent, fetchOrganizers, uploadImage } from '../../../services/api';
import './EventsTab.css';

function EventsTab() {
  const { t } = useTranslation();
  const [events, setEvents] = useState([]);
  const [organizers, setOrganizers] = useState([]);
  const [formData, setFormData] = useState({
    name: '',
    description: '',
    dateOfEvent: '',
    price: '',
    currency: 'SAR',
    ticketsAvailable: '',
    purchaseStartDate: '',
    purchaseEndDate: '',
    organizers: [],
    image: '',
  });
  const [editEventId, setEditEventId] = useState(null);
  const [uploading, setUploading] = useState(false);

  useEffect(() => {
    const loadEvents = async () => {
      try {
        const data = await fetchEvents();
        setEvents(data);
      } catch (error) {
        console.error(t('eventsTab.alerts.fetchError'), error);
      }
    };

    const loadOrganizers = async () => {
      try {
        const data = await fetchOrganizers();
        setOrganizers(data);
      } catch (error) {
        console.error(t('eventsTab.alerts.fetchOrganizersError'), error);
      }
    };

    loadEvents();
    loadOrganizers();
  }, [t]);

  const handleEventSubmit = async (e) => {
    e.preventDefault();

    if (formData.organizers.length === 0) {
      alert(t('eventsTab.alerts.selectOrganizer'));
      return;
    }

    const eventData = new FormData();
    eventData.append('name', formData.name);
    eventData.append('description', formData.description);
    eventData.append('dateOfEvent', formData.dateOfEvent);
    eventData.append('price', formData.price);
    eventData.append('currency', formData.currency);
    eventData.append('ticketsAvailable', formData.ticketsAvailable);
    eventData.append('purchaseStartDate', formData.purchaseStartDate);
    eventData.append('purchaseEndDate', formData.purchaseEndDate);
    eventData.append('organizers', JSON.stringify(formData.organizers));
    if (formData.image) {
      eventData.append('image', formData.image);
    }
    if (editEventId) {
      eventData.append('eventId', editEventId);
    }

    try {
      const response = await fetch('/api/events/create-or-update', {
        method: 'POST',
        body: eventData,
      });
      if (!response.ok) throw new Error('Failed to save event');
      const updatedEvents = await response.json();
      setEvents(updatedEvents);
      alert(editEventId ? t('eventsTab.alerts.updated') : t('eventsTab.alerts.created'));
      resetForm();
    } catch (error) {
      console.error(t('eventsTab.alerts.submitError'), error);
    }
  };

  const resetForm = () => {
    setFormData({
      name: '',
      description: '',
      dateOfEvent: '',
      price: '',
      currency: 'SAR',
      ticketsAvailable: '',
      purchaseStartDate: '',
      purchaseEndDate: '',
      organizers: [],
      image: '',
    });
    setEditEventId(null);
  };

  const handleEditEvent = (event) => {
    setEditEventId(event._id);
    setFormData({
      name: event.name,
      description: event.description,
      dateOfEvent: event.dateOfEvent.slice(0, 10),
      price: event.price,
      currency: event.currency,
      ticketsAvailable: event.ticketsAvailable,
      purchaseStartDate: event.purchaseStartDate.slice(0, 10),
      purchaseEndDate: event.purchaseEndDate.slice(0, 10),
      organizers: event.organizers,
      image: event.image || '',
    });
  };

  const handleDeleteEvent = async (id) => {
    try {
      await deleteEvent(id);
      alert(t('eventsTab.alerts.deleted'));
      setEvents(events.filter((event) => event._id !== id));
    } catch (error) {
      console.error(t('eventsTab.alerts.deleteError'), error);
    }
  };

  const handleOrganizerSelection = (organizerId) => {
    setFormData((prevFormData) => {
      const organizers = prevFormData.organizers.includes(organizerId)
        ? prevFormData.organizers.filter((id) => id !== organizerId)
        : [...prevFormData.organizers, organizerId];
      return { ...prevFormData, organizers };
    });
  };

  const handleImageUpload = async (e) => {
    const file = e.target.files[0];
    if (!file) return;

    setUploading(true);
    try {
      const { url } = await uploadImage(file);
      setFormData({ ...formData, image: url });
      alert(t('eventsTab.alerts.imageUploaded'));
    } catch (error) {
      console.error(t('eventsTab.alerts.imageUploadError'), error);
    } finally {
      setUploading(false);
    }
  };

  return (
    <div className="events-tab">
      <h2 className="tab-title">{t(editEventId ? 'eventsTab.title.edit' : 'eventsTab.title.create')}</h2>
      <form className="event-form" onSubmit={handleEventSubmit}>
        <input
          type="text"
          placeholder={t('eventsTab.form.eventName')}
          value={formData.name}
          onChange={(e) => setFormData({ ...formData, name: e.target.value })}
          required
        />
        <textarea
          placeholder={t('eventsTab.form.description')}
          value={formData.description}
          onChange={(e) => setFormData({ ...formData, description: e.target.value })}
          required
        />
        <input
          type="date"
          value={formData.dateOfEvent}
          onChange={(e) => setFormData({ ...formData, dateOfEvent: e.target.value })}
          required
        />
        <input
          type="number"
          placeholder={t('eventsTab.form.price')}
          value={formData.price}
          onChange={(e) => setFormData({ ...formData, price: e.target.value })}
          required
        />
        <select
          value={formData.currency}
          onChange={(e) => setFormData({ ...formData, currency: e.target.value })}
          required
        >
          <option value="SAR">SAR</option>
          <option value="AED">AED</option>
          <option value="KWD">KWD</option>
          <option value="BHD">BHD</option>
          <option value="OMR">OMR</option>
          <option value="QAR">QAR</option>
        </select>
        <input
          type="number"
          placeholder={t('eventsTab.form.ticketsAvailable')}
          value={formData.ticketsAvailable}
          onChange={(e) => setFormData({ ...formData, ticketsAvailable: e.target.value })}
          required
        />
        <input
          type="date"
          value={formData.purchaseStartDate}
          onChange={(e) => setFormData({ ...formData, purchaseStartDate: e.target.value })}
          required
        />
        <input
          type="date"
          value={formData.purchaseEndDate}
          onChange={(e) => setFormData({ ...formData, purchaseEndDate: e.target.value })}
          required
        />
        <input type="file" accept="image/*" onChange={handleImageUpload} />
        {uploading && <p>{t('eventsTab.alerts.uploading')}</p>}
        {formData.image && (
          <div className="image-preview">
            <img src={formData.image} alt="Event" style={{ width: '100%', maxHeight: '150px', objectFit: 'cover' }} />
          </div>
        )}
        <div className="organizers-container">
          <label>{t('eventsTab.form.organizers')}</label>
          {organizers.map((organizer) => (
            <div key={organizer._id} className="organizer-item">
              <input
                type="checkbox"
                value={organizer._id}
                checked={formData.organizers.includes(organizer._id)}
                onChange={() => handleOrganizerSelection(organizer._id)}
              />
              <span>
                {organizer.fullName} ({organizer.email})
              </span>
            </div>
          ))}
        </div>
        <button type="submit" className="submit-button">
          {t(editEventId ? 'eventsTab.form.submit.update' : 'eventsTab.form.submit.create')}
        </button>
      </form>

      <h2 className="tab-title">{t('eventsTab.title.existing')}</h2>
      <ul className="events-list">
        {events.map((event) => (
          <li key={event._id} className="event-item">
            <img
              src={event.image || '/default-image.jpg'}
              alt={event.name}
              style={{ width: '100px', height: '100px', objectFit: 'cover' }}
            />
            <h3>{event.name}</h3>
            <p>{t('eventsTab.event.currency')}: {event.currency}</p>
            <button onClick={() => handleEditEvent(event)} className="edit-button">
              {t('eventsTab.actions.edit')}
            </button>
            <button onClick={() => handleDeleteEvent(event._id)} className="delete-button">
              {t('eventsTab.actions.delete')}
            </button>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default EventsTab;
